import{_ as t,c as e,o as a,V as r}from"./chunks/framework.79590ae5.js";const u=JSON.parse('{"title":"前端的绝对路径和相对路径","description":"","frontmatter":{"title":"前端的绝对路径和相对路径","date":"2022/12/08 16:33:25","summary":null,"config":{"top":false,"dir":true,"dirTag":["h3","h4","h5"],"tag":[],"valine":true,"valineId":null},"password":false},"headers":[],"relativePath":"views/blog/other/QianDuanDeJueDuiLuJingHeXiangDuiLuJing.md"}'),l={name:"views/blog/other/QianDuanDeJueDuiLuJingHeXiangDuiLuJing.md"},i=r('<p>WEB中的绝对路径和相对路径是跟你的<strong>所在文件目录没有关系</strong>，而是跟当前资源的-&gt;URL&lt;-有关</p><h3 id="绝对路径" tabindex="-1">绝对路径 <a class="header-anchor" href="#绝对路径" aria-label="Permalink to &quot;绝对路径&quot;">​</a></h3><p>与当前资源的-&gt;path&lt;-无关</p><p>例如：当前资源：<code><a href="http://a.com/news/detail?id=1" target="_blank" rel="noreferrer">http://a.com/news/detail?id=1</a></code></p><ul><li>-&gt;<a href="http://a.com/1" target="_blank" rel="noreferrer">http://a.com/1</a>&lt;- =&gt; -&gt;<a href="http://a.com/1" target="_blank" rel="noreferrer">http://a.com/1</a>&lt;-</li><li>-&gt;<a href="/a.com/1.html">//a.com/1</a>&lt;- =&gt; -&gt;<a href="http://a.com/1" target="_blank" rel="noreferrer">http://a.com/1</a>&lt;- （省略了协议的绝对路径，会使用当前资源的协议）</li><li>-&gt;/1&lt;- =&gt; -&gt;<a href="http://a.com/1" target="_blank" rel="noreferrer">http://a.com/1</a>&lt;- （省略了协议、域名、端口的绝对路径，会使用当前资源的协议、域名、端口）</li><li>-&gt;/&lt;- =&gt; -&gt;<a href="http://a.com/" target="_blank" rel="noreferrer">http://a.com/</a>&lt;- （省略了协议、域名、端口的绝对路径，会使用当前资源的协议、域名、端口，其实就是根域名）</li></ul><h4 id="使用场景" tabindex="-1">使用场景 <a class="header-anchor" href="#使用场景" aria-label="Permalink to &quot;使用场景&quot;">​</a></h4><ul><li><p>1.<strong>站外资源</strong>只能使用绝对路径 -&gt;iconfont的css、站外图片、站外链接等&lt;-</p></li><li><p>2.当前资源和目标资源的<strong>相对位置不稳定或不明确</strong>，且目标资源的path是稳定的，推荐绝对路径，-&gt;用户上传的图片地址、多地址的页面引入同一资源等&lt;-</p></li></ul><h3 id="相对路径" tabindex="-1">相对路径 <a class="header-anchor" href="#相对路径" aria-label="Permalink to &quot;相对路径&quot;">​</a></h3><p>相对于当前资源-&gt;path&lt;-路径</p><p>例如：当前资源：<code><a href="http://a.com/news/detail?id=1" target="_blank" rel="noreferrer">http://a.com/news/detail?id=1</a></code></p><ul><li>-&gt;./list&lt;- =&gt; -&gt;<a href="http://a.com/news/list" target="_blank" rel="noreferrer">http://a.com/news/list</a>&lt;-</li><li>-&gt;../list&lt;- =&gt; -&gt;<a href="http://a.com/list" target="_blank" rel="noreferrer">http://a.com/list</a>&lt;- （../表示当前资源上一段path）</li><li>-&gt;list&lt;- =&gt; -&gt;<a href="http://a.com/news/list" target="_blank" rel="noreferrer">http://a.com/news/list</a>&lt;- （与./一样，表示当前资源path）</li><li>-&gt;?id=2&lt;- =&gt; -&gt;<a href="http://a.com/news/detail?id=2" target="_blank" rel="noreferrer">http://a.com/news/detail?id=2</a>&lt;- （以?前的path为起始，拼接）</li><li>-&gt;#css&lt;- =&gt; -&gt;<a href="http://a.com/news/detail?id=1#css" target="_blank" rel="noreferrer">http://a.com/news/detail?id=1#css</a>&lt;- （以全部path为起始，拼接）</li></ul>',11),n=[i];function o(h,s,g,c,p,d){return a(),e("div",null,n)}const f=t(l,[["render",o]]);export{u as __pageData,f as default};
